import os
import random
from PyQt5.QtWidgets import QApplication, QLabel, QMainWindow
from PyQt5.QtGui import QPixmap
from PyQt5.QtCore import QUrl, QTimer, Qt
from PyQt5.QtMultimedia import QMediaPlayer, QMediaContent



#___  ____ _ _ _ ____ ____    ___  _    ____ _  _ ___
#|__] |  | | | | |___ |__/    |__] |    |__| |\ |  |
#|    |__| |_|_| |___ |  \    |    |___ |  | | \|  |
                

#OPENING | https://www.youtube.com/watch?v=_85LaeTCtV8 :3



class ImageLoop:
    def __init__(self, window_size=(8000, 3000), image_folder='crow', image_duration=100):

        #Initialisation des variables
        self.window_size = window_size
        self.image_folder = image_folder
        self.image_duration = image_duration
        self.images = []
        self.current_image_index = 0
        self.label = None
        self.main_window = None
        self.app = None
        self.sound = QMediaPlayer()




#Fonction pour mettre à jour les images jouées
    def update_image(self):

        #On affiche une image puis on passe à la suivante
        self.label.setPixmap(self.images[self.current_image_index])
        self.current_image_index = (self.current_image_index + 1) % len(self.images)

        QTimer.singleShot(self.image_duration, self.update_image)
    
        #Probabilité de 10% (0.1) de jouer un son
        if random.random() < 0.1:
            sound_file = os.path.join('se', 'aho.mp3')
            self.sound.setMedia(QMediaContent(QUrl.fromLocalFile(sound_file)))

            self.sound.play()



#Fonction pour arreter l'application
    def stop(self):
        self.app.exit()

    
#Création de l'application et de la fenêtre principale
    def start(self) :

        self.app = QApplication([])
        self.main_window = QMainWindow()
        
        self.main_window.setWindowFlags(Qt.FramelessWindowHint | Qt.WindowStaysOnTopHint)  #On la superpose a toute nos fenetres
        self.main_window.setAttribute(Qt.WA_TranslucentBackground) #Le fond évidemment transparent

        #Fixe la taille de la fenêtre principale en utilisant les dimensions définies dans self.window_size [0] = largeur [1] = hauteur
        self.main_window.setFixedSize(self.window_size[0], self.window_size[1])
        self.main_window.move(100, 100)
        
        #On affiche les images dans un label au fond transpparent
        self.label = QLabel(self.main_window)
        self.label.setGeometry(0, 0, self.window_size[0], self.window_size[1])
        self.label.setStyleSheet("background-color: transparent;")
         
        #Configuration de la fenêtre principale
        self.main_window.setCentralWidget(self.label)
        self.main_window.show()

        #Chargement des images à partir du dossier spécifié
        for file_name in os.listdir(self.image_folder):

            #Charge une image à partir du fichier, la redimensionne aux dimensions de la fenêtre et stocke l'objet QPixmap résultant dans "image"
            image = QPixmap(os.path.join(self.image_folder, file_name)).scaled(self.window_size[0], self.window_size[1])
            self.images.append(image)

        #Mise à jour de l'image affichée
        self.update_image()
        self.app.exec()


